mapper.select:
SELECT *
FROM SYS_OPTION a
WHERE id = #{id}

SELECT a.option_code,a.option_value
FROM SYS_OPTION a
WHERE 1 = 1

	AND a.option_id = #{optionId}


	AND a.option_code = #{optionCode}


	AND a.state #{state}

ORDER BY a.id

mapper.delete:
DELETE FROM SYS_OPTION

	
		WHERE 1 != 1
	
	
		WHERE `id` =
		#{id}
	
	
		WHERE `id` in
		#{id}
